$("#timespan").change(function(){var e=new Date,t=new Date;switch(this.value){case"today":e.setHours(0),e.setMinutes(0);break;case"week":e.setDate(e.getDate()-e.getDay()),e.setHours(0),e.setMinutes(0);break;case"month":e.setDate(1),e.setHours(0),e.setMinutes(0);break;case"three":e.setMonth(e.getMonth()-3);break;case"six":e.setMonth(e.getMonth()-6);break;case"year":e.setDate(1),e.setMonth(0),e.setHours(0),e.setMinutes(0);break;case"yearthree":e.setYear(e.getFullYear()-3);break;case"yearfive":e.setYear(e.getFullYear()-5);break;case"all":return $("#start-date").val("____/__/__ __:__").trigger("change"),void $("#end-date").val("____/__/__ __:__").trigger("change")}var a=e.getFullYear()+"/"+("0"+(e.getMonth()+1)).slice(-2)+"/"+("0"+e.getDate()).slice(-2)+" "+("0"+e.getHours()).slice(-2)+":"+("0"+e.getMinutes()).slice(-2),s=t.getFullYear()+"/"+("0"+(t.getMonth()+1)).slice(-2)+"/"+("0"+t.getDate()).slice(-2)+" "+("0"+t.getHours()).slice(-2)+":"+("0"+t.getMinutes()).slice(-2);$("#start-date").val(a).trigger("change"),$("#end-date").val(s).trigger("change")});